select dept.Deptname,dept.deptid ,(select count(1) from CD_Inpatient i1 left join CD_MedicalRecordHomePage m1 on i1.inpatientid=m1.inpatientid where m1.LmminenceGrave=1 and i1.CurrentDeptID=dept.deptid
i1.EntryTime > to_date('#Start# 00:00:00','yyyy-mm-dd hh24:mi:ss') i1.EntryTime < to_date('#Stop# 23:59:59','yyyy-mm-dd hh24:mi:ss')
) wzs ,(select count(1) from CD_Inpatient i2 where i2.currentdeptid=dept.deptid
i2.EntryTime > to_date('#Start# 00:00:00','yyyy-mm-dd hh24:mi:ss') i2.EntryTime < to_date('#Stop# 23:59:59','yyyy-mm-dd hh24:mi:ss')
) yybr ,(select count(1) from CD_Inpatient i3 where i3.AdmissionDept=dept.deptid
i3.EntryTime > to_date('#Start# 00:00:00','yyyy-mm-dd hh24:mi:ss') i3.EntryTime < to_date('#Stop# 23:59:59','yyyy-mm-dd hh24:mi:ss')
) rybr ,(select count(1) from CD_TRANSFER t1 left join CD_INPATIENT i4 on t1.inpatientid=i4.inpatientid where t1.currentdeptid=dept.deptid
i4.EntryTime > to_date('#Start# 00:00:00','yyyy-mm-dd hh24:mi:ss') i4.EntryTime < to_date('#Stop# 23:59:59','yyyy-mm-dd hh24:mi:ss')
) tkzr ,(select count(1) from CD_Inpatient i5 where (i5.LeaveDept=dept.deptid or (i5.LeaveTime is not null and i5.CurrentDeptID=dept.deptid))
i5.LeaveTime > to_date('#Start# 00:00:00','yyyy-mm-dd hh24:mi:ss') i5.LeaveTime < to_date('#Stop# 23:59:59','yyyy-mm-dd hh24:mi:ss')
) cybr ,(select count(1) from CD_Inpatient i6 left join CD_MedicalRecordHomePage m2 on i6.inpatientid=m2.inpatientid where m2.LmminenceGrave=1 and i6.CurrentDeptID=dept.deptid
i6.EntryTime > to_date('#Start# 00:00:00','yyyy-mm-dd hh24:mi:ss') i6.EntryTime < to_date('#Stop# 23:59:59','yyyy-mm-dd hh24:mi:ss')
) zy ,(select count(1) from CD_Inpatient i7 left join CD_MedicalRecordHomePage m3 on i7.inpatientid=m3.inpatientid where m3.LmminenceGrave=2 and i7.CurrentDeptID=dept.deptid
i7.EntryTime > to_date('#Start# 00:00:00','yyyy-mm-dd hh24:mi:ss') i7.EntryTime < to_date('#Stop# 23:59:59','yyyy-mm-dd hh24:mi:ss')
) hz ,(select count(1) from CD_Inpatient i8 left join CD_MedicalRecordHomePage m4 on i8.inpatientid=m4.inpatientid where m4.LmminenceGrave=3 and i8.CurrentDeptID=dept.deptid
i8.EntryTime > to_date('#Start# 00:00:00','yyyy-mm-dd hh24:mi:ss') i8.EntryTime < to_date('#Stop# 23:59:59','yyyy-mm-dd hh24:mi:ss')
) wy ,(select count(1) from CD_Inpatient i9 left join CD_MedicalRecordHomePage m5 on i9.inpatientid=m5.inpatientid where m5.LmminenceGrave=4 and i9.CurrentDeptID=dept.deptid
i9.EntryTime > to_date('#Start# 00:00:00','yyyy-mm-dd hh24:mi:ss') i9.EntryTime < to_date('#Stop# 23:59:59','yyyy-mm-dd hh24:mi:ss')
) sw ,(select count(1) from CD_Inpatient i10 left join CD_MedicalRecordHomePage m6 on i10.inpatientid=m6.inpatientid where m6.LmminenceGrave=5 and i10.CurrentDeptID=dept.deptid
i10.EntryTime > to_date('#Start# 00:00:00','yyyy-mm-dd hh24:mi:ss') i10.EntryTime < to_date('#Stop# 23:59:59','yyyy-mm-dd hh24:mi:ss')
) qt ,(select count(1) from CD_TRANSFER t2 left join CD_INPATIENT i11 on t2.inpatientid=i11.inpatientid where t2.OldDeptID=dept.deptid
i11.EntryTime > to_date('#Start# 00:00:00','yyyy-mm-dd hh24:mi:ss') i11.EntryTime < to_date('#Stop# 23:59:59','yyyy-mm-dd hh24:mi:ss')
) zcbr from AI_DEPTINFO dept where dept.isinpatient=0 and IsCance=0
dept.deptid=#DeptID#
